$(function(){$("#raffleButton").click(function(){for(var f=$("#numberOfAttendees"),e=$("#numberOfPrizes"),o=f.val(),s=e.val(),t=[],i,r,u,n=0;n<o;n++)t.push(n);for(i=$("#results"),i.html(""),n=0;n<s;n++)r=Math.floor(Math.random()*t.length),u=t.splice(r,1)[0],i.append("Attendee "+(u+1)+" wins prize "+(n+1)+"<br/>")})})